import axios from 'axios';

export const uploadPost = (image, description) => async (dispatch) => {
  const formData = new FormData();
  formData.append('image', image);
  formData.append('description', description);

  try {
    const res = await axios.post(`${process.env.REACT_APP_API_URL}/posts/`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    dispatch({
      type: 'UPLOAD_POST_SUCCESS',
      payload: res.data,
    });
  } catch (error) {
    dispatch({
      type: 'UPLOAD_POST_FAIL',
      payload: error.response.data.message || 'Error uploading post',
    });
  }
};
